import React, {useState, useContext, useEffect} from 'react'
import ContactContext from '../../context/contact/contactContext';

const ContactForm = () => {
  const contactContext = useContext(ContactContext);
  const {addContact, updateContact, clearCurrent, current} = contactContext;
  useEffect(() => {
    if(current!=null) {
      setContact(current);
    }else{
      setContact({
        title: '',
        desc: '',
        type: 'no'
      });
    }
  }, [doubtContext, current]);
  const [doubt, setDoubt] = useState({
    title: '',
    desc: '',
    type: 'no'
  });

  const {title, desc, type} = doubt;
  const onChange= e =>
  setContact({...doubt, [e.target.name]: e.target.value});

  const onSubmit = e => {
    e.preventDefault();
    if(current === null){
      addContact(doubt);
    }else{
      updateContact(doubt);
    }

    setContact({
      title: '',
      desc: '',
      type: 'no'
    });
  };
 const clearAll = () => {
   clearCurrent();
 }

  return (
    <form onSubmit={onSubmit}>
      <h2 className='text-primary'>{current ? 'Edit Doubt' : 'Add Doubt'}</h2>
      <input type="text" placeholder="Title" name="name" value={title}
      onChange={onChange} />
      <textarea placeholder="Desription" name="desc" value={desc} onChange={onChange}/>
      <h5>Submit as anonymous</h5>
      <input type="radio" name="type" value="yes"
      checked={type ==='yes'} onChange={onChange}/>Yes{' '}
      <input type="radio" name="type" value="no"
      checked={type ==='no'} onChange={onChange}/>No
      <div>
       <input type="submit" value={current ? 'Update Doubt' : 'Add Doubt'}
       className="btn btn-primary btn-block"/>
      </div>
      {current && <div>
      <button className = "btn btn-light btn-block" onClick={clearAll}>Clear</button>
      </div>}
    </form>
  );
};

export default ContactForm;
